com.mirth.connect.plugins.javascriptstep.script:
//IF THE FT1 SEGMENT HAD MULTIPLE LINES THEN WE COULD CALCULATE A TOTAL AMOUNT FOR THAT PATIENT

var total = '';

//ITERATE THROUGH EACH FT1 SEGMENT AND ADD UP THE AMOUTS
for (i=0; i<msg['FT1'].size(); i++) {
	total += parseInt($('amount'));
}

//MAP THE FINAL TOTAL TO THE CHANNEL MAP FOR USE IN DESTINATIONS
channelMap.put('grandTotal', total);

//STORE SPECIFIC INFORMATION IN CHANNEL MAP FOR USE IN DESTINATIONS
var patientID = msg['PID']['PID.3']['PID.3.1'].toString();
var firstName = msg['PID']['PID.5']['PID.5.2'].toString();
var lastName = msg['PID']['PID.5']['PID.5.1'].toString();
var DOB = msg['PID']['PID.7']['PID.7.1'].toString();
var rendProvider = msg['PV1']['PV1.7']['PV1.7.1'].toString();

channelMap.put('patientID', patientID);
channelMap.put('patientFirstName', firstName);
channelMap.put('patientLastName', lastName);
channelMap.put('patientDOB', DOB);
channelMap.put('renderingProvider', rendProvider);

com.mirth.connect.plugins.javascriptrule.script:
//FILTER FOR SPECIFIC RENDERING PROVIDER OR ANY NUMBER OF DATA POINTS
//WE COULD USE .CONTAINS ALSO
if($('renderingProvider').toString() == "dr stash"){
	return true;
}


















