com.mirth.connect.plugins.javascriptstep.script:
//CREATE A JSON MESSAGE AND STORE IT IN A CHANNEL MAP VARIABLE
var jsonPacket = {};

jasonPacket.header = 'Patient:';
jasonPacket.firstName = msg['PID']['PID.5']['PID.5.2'].toString();
jasonPacket.lastName = msg['PID']['PID.5']['PID.5.1'].toString();

//FORMAT THE DATE USING A STORED PROCEDURE/CODE TEMPLATE LIBRARY (MOMENT)
jasonPacket.DOB = moment(msg['PID']['PID.7']['PID.7.1'].toString(), 'MM-DD-YYYY').format('MMDDYYYY');
jasonPacket.rendProvider = msg['PV1']['PV1.7']['PV1.7.1'].toString();

channelMap.put('jsonPacketForJason', jsonPacket);

